var DI      = env.get("Channel");
var Req     = 'IO.DI.'+DI;
msg.payload = global.get(Req,'memoryOnly');
return msg;

var AI      = env.get("Channel");
var Req     = 'IO.AI.'+AI;
msg.payload = global.get(Req,'memoryOnly');
return msg;

//Get user data from flow context
var user            = flow.get("user")|| "null";
var password        = flow.get("password")||"null";

//User status
var UserStatus = global.get("UserStatus","memoryOnly")||0;

// Admin user
var AdminUser       = "slacol";
var AdminPassword   = "1278";

// Operator user
var OperUser        = "Oper";
var OperPassword    = "5321";

// Operator user
var MantUser        = "mant";
var MantPassword    = "6152";

// root user
var rootUser = "root";
var rootPassword = "1009";

if(!UserStatus){

    // Acceso Nivel 1
    if(user == AdminUser && password == AdminPassword){
        global.set("UserStatus", 2,"memoryOnly");
        msg.name = "Admin";
        msg.userstatus = "Sesión iniciada"
    }
    // Acceso Nivel 2
    else if((user == OperUser && password == OperPassword)||(user == MantUser && password == MantPassword)){
        global.set("UserStatus", 1,"memoryOnly");
        msg.name = "Operator";
        msg.userstatus = "Sesión iniciada"
    }
    // Acceso Nivel root
    else if (user == rootUser && password == rootPassword) {
        global.set("UserStatus", 3, "memoryOnly");
        msg.name = "root";
        msg.userstatus = "Sesión iniciada"
    }
    else{
        msg.error = "Usuario o contraseña incorrectos: Por favor llene los campos nuevamente";
    }
}
else{
    msg.error = "Usuario activo, por favor cierre sesión primero";
}

msg.payload = global.get("UserStatus","memoryOnly")

return msg;

var Active0     = flow.get("Active0",'memoryOnly');
var Active1     = flow.get("Active1",'memoryOnly');
var status = (global.get("C2S.Drive_Status.Active","memoryOnly")||0) ==1 ? true : false;
var Arranque    = ((msg.command == "Arranque 'Forward'") || (msg.command=="Arranque 'Reverse'")) ? true:false;
var Parada      = msg.command == "Parada del equipo" ? true:false;
var stopping = global.get("C2S.Implicit.Stopping","memoryOnly")||false;
delete msg.topic;
// Arranque exitoso
if(Arranque && Active0 == 0 && Active0 != Active1){
    msg.payload ="Arranque exitoso";
    global.set("C2S.FlagStatus",true);
}
// Arranque fallido
else if(Arranque && Active0 == 0 && Active0 == Active1){
    msg.payload = "Arranque fallido";
}
// Parada Exitosa
else if (Parada && (stopping||!status)){
    msg.payload = "Parada exitosa";
    global.set("C2S.FlagStatus", false);
}
// Parada Fallida
else if (Parada && (!stopping || status)){
    msg.payload = "Parada fallida";
}
else{
    return;
}
return msg;

var Active = (global.get("C2S.Drive_Status.Active", "memoryOnly") || 0) == 1 ? true : false;
var Accel = (global.get("C2S.Drive_Status.Accel", "memoryOnly") || 0) == 1 ? true : false;
var Decel = (global.get("C2S.Drive_Status.Decel", "memoryOnly") || 0) == 1 ? true : false;

// Setpoints
var Prot = env.get('protection')||"test";
var querySpHH   = "Protections."+Prot+".HH.Setpoint";
var querySpH    = "Protections."+Prot+".H.Setpoint";
var querySpL    = "Protections."+Prot+".L.Setpoint";
var querySpLL   = "Protections."+Prot+".LL.Setpoint";

var HHSetpoint  = global.get(querySpHH)||0;
var HSetpoint   = global.get(querySpH)||0;
var LSetpoint   = global.get(querySpL)||0;
var LLSetpoint  = global.get(querySpLL)||0;

// Enablers
var queryEnHH   = "Protections."+Prot+".HH.EN";
var queryEnH    = "Protections."+Prot+".H.EN";
var queryEnL    = "Protections."+Prot+".L.EN";
var queryEnLL   = "Protections."+Prot+".LL.EN";

var HH_EN       = global.get(queryEnHH)||false;
var H_EN        = global.get(queryEnH)||false;
var L_EN        = global.get(queryEnL)||false;
var LL_EN       = global.get(queryEnLL)||false;

// Death times
var queryDtHH   = "Protections."+Prot+".HH.DT";
var queryDtH    = "Protections."+Prot+".H.DT";
var queryDtL    = "Protections."+Prot+".L.DT";
var queryDtLL   = "Protections."+Prot+".LL.DT";

var HHDeathTime = global.get(queryDtHH)||0;
var HDeathTime  = global.get(queryDtH)||0;
var LDeathTime  = global.get(queryDtL)||0;
var LLDeathTime = global.get(queryDtLL)||0;

// Value
var CurrentValue = msg.payload;

if (CurrentValue >= HHSetpoint && HH_EN === true) {
    msg.disp = true;
    msg.alrm = false;
    msg.message = "Trigger by HH Protection";
    msg.delay = HHDeathTime * 1000;
    msg.state = 4;
}
else if (CurrentValue >= HSetpoint && H_EN === true) {
    msg.disp = false;
    msg.alrm = true;
    msg.message = "Alarm by H Protection";
    msg.delay = HDeathTime * 1000;
    msg.state = 2;
}
else if (CurrentValue <= LLSetpoint && LL_EN === true && Active && !Accel && !Decel) {
    msg.disp = true;
    msg.alrm = false;
    msg.message = "Trigger by LL Protection"
    msg.delay = LLDeathTime * 1000;
    msg.state = 3;
}
else if (CurrentValue <= LSetpoint && L_EN === true && Active && !Accel && !Decel) {
    msg.disp = false;
    msg.alrm = true;
    msg.message = "Alarm by L Protection";
    msg.delay = LDeathTime * 1000;
    msg.state = 1;
}
else {
    msg.disp = false;
    msg.alrm = false;
    msg.message = "Normal condition";
    msg.reset = true;
    msg.state = 0;
}
return msg;

var Act = msg.disp;
var Ant = context.get("Anterior")||false;
var Output = { 
    payload: false,
    Mensaje: msg.message
};

// Actualizar el contexto
context.set("Anterior", Act);

//Flanco de subida
if(Act === true && Ant === false){
    context.set("RefTime", Date.now());
}
else if(Act === true && Ant === true){
    if(Date.now() - context.get("RefTime") >= msg.delay){
        Output.payload = true;
    }
}
return Output;

var Act = msg.alrm;
var Ant = context.get("Anterior")||false;
var Output = { 
    payload: false,
    Mensaje: msg.message
};

// Actualizar el contexto
