// Date and Time Object
var DT      = new Date();

// Date
var year    = DT.getFullYear();
var month   = DT.getMonth()+1;
var day     = DT.getDate();

// Time
var hours   = DT.getHours();
var minutes = DT.getMinutes();  minutes = minutes <= 9 ? "0"+minutes : minutes;
var seconds = DT.getSeconds();  seconds = seconds <= 9 ? "0"+seconds : seconds;

// Payload
msg.payload = day+"/"+month+"/"+year+" "+hours+":"+minutes+":"+seconds;

return msg;

var Output_Frequency    = global.get("C2S.Implicit.Output_Frequency",'memoryOnly')||0;
var Power_Factor        = global.get("C2S.Implicit.Power_Factor",'memoryOnly')||0;
var Output_Voltage      = global.get("C2S.Implicit.Output_Voltage", 'memoryOnly') || 0;
var Bus_DC_Volts        = global.get("C2S.Implicit.Bus_DC_Volts", 'memoryOnly') || 0;
var Output_Power        = global.get("C2S.Implicit.Output_Power", 'memoryOnly') || 0;
var Output_Current      = global.get("C2S.Implicit.Output_Current", 'memoryOnly') || 0;
var Elpsd_Mtr_kWHrs     = global.get("C2S.Implicit.Elpsd_Mtr_kWHrs", 'memoryOnly') || 0;


var MtrCurrent;
var MtrVoltage;

var MaxLimit = global.get("C2S.Speed_Control.MaxLimit")||1;
var Speed = global.get("C2S.Implicit.Output_Frequency",'memoryOnly')||0;
var SpeedPercentage = (Math.abs(Speed) / MaxLimit)*100;

var MtrCtrl = global.get("C2S.Motor_Control.Type") || "IM";
var Rtx;

// Corriente y tensión de motor
Rtx = global.get("C2S.RelTx") || 1;
MtrVoltage = Output_Voltage * Rtx;
MtrCurrent = Output_Current / Rtx;
flow.set("Current_Motor",MtrCurrent,"memoryOnly");

var Secondary_voltage=global.get("C2S.IM.TX.Secondary_voltage");


//Limites maximos pantalla

var Limite_Voltage_HH = global.get("Protections.Voltage.HH.Setpoint") || 100;
var Limite_Load_Mtr_HH = global.get("Protections.Load.HH.Setpoint") || 100;
var Limite_Load_HH = (Limite_Load_Mtr_HH * Rtx).toFixed(2)||100;
var Limite_BusDC_HH = global.get("Protections.BusDC.HH.Setpoint") || 100;

var Limite_Voltage_LL = global.get("Protections.Voltage.LL.Setpoint") || 0;
var Limite_Load_Mtr_LL = global.get("Protections.Load.LL.Setpoint") || 0;
var Limite_Load_LL = (Limite_Load_Mtr_LL * Rtx ).toFixed(2)|| 0;
var Limite_BusDC_LL = global.get("Protections.BusDC.LL.Setpoint") || 0;


return[
    { payload: catcherror(Output_Frequency, 3) + " " + "Hz", control: { min: 0, max: MaxLimit }},
    { payload: catcherror(SpeedPercentage, 0) + " " + "%", control: { min: 0, max: 100 }},
    { payload: catcherror(Power_Factor, 3) + " " + "", control: { min: 0, max: 1 }},
    { payload: catcherror(Output_Current, 3) + " " + "A", control: { min: parseInt(Limite_Load_LL), max: parseInt(Limite_Load_HH) }},
    { payload: catcherror(Bus_DC_Volts, 0) + " " + "V", control: { min: Limite_BusDC_LL, max: Limite_BusDC_HH }},
    { payload: catcherror(MtrVoltage, 1) + " " + "V", control: { min: 10, max: Secondary_voltage }},
    { payload: catcherror(Output_Power, 1) + " " + "kW", control: { min: 10, max: 1000 }},
    { payload: catcherror(Output_Voltage, 1) + " " + "V", control: { min: 0, max: 480 }},
    { payload: catcherror(MtrCurrent, 1) + " " + "A", control: { min: catcherror(Limite_Load_Mtr_LL, 2), max: catcherror(Limite_Load_Mtr_HH,2) }},
    { payload: catcherror(Elpsd_Mtr_kWHrs, 1) + " " + "kWHrs", control: { min: 0, max: 1000 } },

]

function catcherror(variable,dec){
    return parseFloat(variable.toFixed(dec));
}

var Process_data = global.get("Process_data.DHS",'memoryOnly')||[];

// Protecciones Instrumentación
var THP = global.get("Process_data.AI['Presión de cabeza THP']", 'memoryOnly') || 0;
var THT = global.get("Process_data.AI['Temperatura de cabeza THT']", 'memoryOnly') || 0;

// Sensor de Fondo
var Vibration_X = Process_data["Vibración X"] || "--";
var Vibration_Z = Process_data["Vibración Z"] || "--";

//Protecciones Fondo
var LeakageCurrent = Process_data["Corriente de fuga"] || "--";
var IntakePressure = Process_data["Presión de entrada"] || "--";
var DischargePressure = Process_data["Presión de descarga"] || "--";
var IntakeTemperature = parseFloat(Process_data["Temperatura de entrada"] || "--");
var MotorTemperature = parseFloat(Process_data["Temperatura de motor"] || "--");
var DischargeTemperature = parseFloat(Process_data["Temperatura de descarga"] || "--");

return [
    { payload: DecPlacces(IntakePressure, 1, "psi") },
    { payload: DecPlacces(DischargePressure, 1, "psi") },
    { payload: DecPlacces(IntakeTemperature, 1, "ºF") },
    { payload: DecPlacces(DischargeTemperature, 1, "ºF") },
    { payload: DecPlacces(MotorTemperature, 1, "ºF") },
    { payload: DecPlacces(Vibration_X, 3, "G") },
    { payload: DecPlacces(Vibration_Z, 3, "G") },
    { payload: DecPlacces(LeakageCurrent, 3, "mA") },
    { payload: DecPlacces(THP, 2,"psi") },
    { payload: DecPlacces(THT, 2,"ºF") }
];

function DecPlacces(value, Places, Units) {
    return typeof value == "number" ? parseFloat(value.toFixed(Places))+" "+Units:value;
}

var controlmode = global.get("C2S.Drive_Status.Command_dir","memoryOnly");

if(controlmode == 1){
    msg.payload = "Forward";
    msg.icon = "fa fa-repeat";
}
else{
    msg.payload = "Reverse";
    msg.icon = "fa fa-undo";
}
node.status({ fill: "green", shape: "dot", text: controlmode });
return msg;

