}

var MtrCtrl = global.get("C2S.Motor_Control.Type");
var Rtx,Rel;

if(MtrCtrl == "IM"){
    Rtx = global.get("C2S.IM.TX.Secondary_voltage") || null;
}
else if(MtrCtrl == "PMM"){
    Rtx = global.get("C2S.Motor_Control.PMM.Tension_Secundario")||null;
}
if(Rtx != null){ 
    Rel = Rtx/480;
}
else{
    Rel=1;
}
msg.payload = Rel.toFixed(2);
global.set("C2S.RelTx", Rel);
node.status({fill:"green",shape:"dot",text:msg.payload});
return msg;

