msg.payload = "Cambio de referencia a "+ Math.abs(msg.payload) + " Hz";
msg.topic = "Referencia";
return msg;

msg.payload = "Falla de comunicación, referencia no recibida";
msg.topic = "Referencia";
return msg;

var controlmode = global.get("Input.Operation.ControlMode")||null;
if (controlmode == null){
    controlmode = "Local";
    global.set("Input.Operation.ControlMode",controlmode);
}
msg.payload = controlmode;
return msg;

var controlmode = global.get("Input.Operation.ControlMode")||null;
if (controlmode == null){
    controlmode = "Local";
    global.set("Input.Operation.ControlMode",controlmode);
}
if(controlmode=="Local")
    msg.payload = "¿Desea cambiar el modo de control a remoto?";
else
    msg.payload = "¿Desea cambiar el modo de control a local?";
return msg;

if(msg.payload =="Aceptar"){
    var controlmode = global.get("Input.Operation.ControlMode")||null;
if(controlmode=="Local")
    global.set("Input.Operation.ControlMode","Remoto");
else
    global.set("Input.Operation.ControlMode","Local");
}
return msg;

var Commanded_SpdRef    = Math.abs(global.get("C2S.Implicit.Commanded_SpdRef","memoryOnly")||0);
var Reference           = global.get("Input.Operation.Reference","memoryOnly")||0;
context.set("Commanded_SpdRef", Commanded_SpdRef,"memoryOnly");
context.set("Reference", Reference,"memoryOnly");
Reference = parseFloat(Reference.toFixed(1));
Commanded_SpdRef = parseFloat(Commanded_SpdRef.toFixed(1));

if (Commanded_SpdRef == Reference)
    msg.verify=1;
else
    msg.verify = 0;

return msg;

var numdec = 1;
