// Definitions
var PR          = global.get("Control_functions.Prolonged_Reference")||null;
var Mtr_Speed   = global.get('C2S.Implicit.Output_Frequency','memoryOnly')||0;
Mtr_Speed       = Math.abs(Mtr_Speed);
var URef        = global.get("C2S.Last_Reference.Value");
var Sts         = global.get("C2S.Drive_Status.Active","memoryOnly")||0;
var ant,act,resp;

if(Math.abs(PR.Hz - Mtr_Speed) < 0.005){
    msg.payload = PR.Hz;
    msg.code = 0;
    node.status({fill:"green",shape:"dot",text: Mtr_Speed+ " = "+(PR.Hz).toFixed(2)+" = "+(msg.payload).toFixed(2)+" "+(Math.abs(PR.Hz - Mtr_Speed)/(PR.Hz>Mtr_Speed ? PR.Hz:Mtr_Speed))});
}
else if(PR.Hz < Mtr_Speed){
    ant = context.get("ant") || parseFloat(Mtr_Speed);
    act = parseFloat(Mtr_Speed);
    resp = act < ant? act:ant;

    msg.payload = resp - PR.dHz;
    msg.payload = msg.payload - PR.dHz < PR.Hz ? PR.Hz : msg.payload;
    msg.code = 1;
    context.set("ant",act);
    node.status({fill:"green",shape:"dot",text: Mtr_Speed+ " - "+(PR.dHz).toFixed(2)+" = "+(msg.payload).toFixed(2)});
}
else if(PR.Hz > Mtr_Speed){
    ant = context.get("ant") || parseFloat(Mtr_Speed);
    act = parseFloat(Mtr_Speed);
    resp = act > ant? act:ant;

    msg.payload = resp + PR.dHz;
    msg.payload = msg.payload + PR.dHz > PR.Hz ? PR.Hz : msg.payload;
    msg.payload.code = 1;
    context.set("ant",act);
    node.status({fill:"green",shape:"dot",text: Mtr_Speed+ " + "+(PR.dHz).toFixed(2)+" = "+(msg.payload).toFixed(2)});
}
else{
    msg.payload = Mtr_Speed;
    msg.code = 0;
    context.set("ant",0);
}
if(Sts == 0){
    msg.payload = URef;
}
msg.payload = msg.payload * 1;
flow.set("RefActual",msg.payload,'memoryOnly');
return msg;

var ramp_refprol1 = flow.get("ramp_refprol[0]") || 0;
var ramp_refprol2 = flow.get("ramp_refprol[1]") || 0;
var ramp_refprol3 = flow.get("ramp_refprol[2]") || 0;
var timer;
msg.payload = (ramp_refprol1*3600)+(ramp_refprol2*60)+ramp_refprol3;
if(msg.payload>345600)
 timer = 3600;
else if(msg.payload>259200)
 timer = 2400;
else if (msg.payload>172800)
 timer = 1800;
else if (msg.payload>86400)
 timer = 1200;
else if (msg.payload>43200)
 timer = 600;
else if (msg.payload>21600)
 timer = 300;
else if (msg.payload>10800)
 timer = 120;
else if (msg.payload>5400)
 timer = 60;
else if (msg.payload>2700)
 timer = 30;
else if (msg.payload>1800)
 timer = 15;
else
    timer = 10;

msg. timer = timer;
flow.set("delay",timer*1000,"memoryOnly");

node.status({fill:"green",shape:"dot",text:"Timer : "+ msg.timer + " Seg"});

return msg;

// Definitions
var PRef = global.get("Control_functions.Prolonged_Reference");
var Commanded_SpdRef = global.get("C2S.Implicit.Commanded_SpdRef",'memoryOnly')||0;
var Hz      = PRef.Hz;

var Time = msg.payload;  
PRef.dHz = msg.timer * (Math.abs(Hz - Math.abs(Commanded_SpdRef) )/Time);

node.status({fill:"green",shape:"dot",text:"Delta Hz : "+ PRef.dHz});
global.set("Control_functions.Prolonged_Reference",PRef);
msg.payload = PRef.dHz;
return msg;

var refinal = global.get("Control_functions.Prolonged_Reference.Hz")||0;
var borrar= global.get("Control_functions.ActiveCF.Prolonged_Reference","memoryOnly")||false;
var inicial = flow.get("RefInicial","memoryOnly") || 0;
var velactual = flow.get("RefActual",'memoryOnly')||0; //global.get("C2S.Implicit.Output_Frequency","memoryOnly") || 0;

if (refinal!=0 && inicial!=0 && velactual !=0){
    msg.payload = Math.abs(100 - ((Math.abs(refinal) - Math.abs(velactual)) / (Math.abs(refinal) - Math.abs(inicial)))*100).toFixed(6);
    if(msg.payload < 0 || msg.payload > 100){
        msg.payload = 0;
    }
if (!borrar) msg.payload = "";
return msg;
}

