function evaluarhab() {
    for (i = 0; i < 13; i++) {
        var acu = 0;
        if (protecciones != null) {
            if (typeof protecciones[labelprot[i]] != 'undefined') {
                if (typeof protecciones[labelprot[i]].HH != 'undefined') {
                    if (protecciones[labelprot[i]].HH.EN == true)
                        acu++;
                }
                if (typeof protecciones[labelprot[i]].H != 'undefined') {
                    if (protecciones[labelprot[i]].H.EN == true)
                        acu++;
                }
                if (typeof protecciones[labelprot[i]].L != 'undefined') {
                    if (protecciones[labelprot[i]].L.EN == true)
                        acu++;
                }
                if (typeof protecciones[labelprot[i]].LL != 'undefined') {
                    if (protecciones[labelprot[i]].LL.EN == true)
                        acu++;
                }
                if (acu > 0)
                    estadoprot.push("<font size = 4px><font color = green>Normal");
                else
                    estadoprot.push("<font size = 4px><font color = gray>-----");
            }
            else
                estadoprot.push("<font size = 4px><font color = gray>-----");
        }
        else
            estadoprot.push("<font size = 4px><font color = gray>-----");
    }
}

function evalstatus() {
    for (i = 0; i < 13; i++) {
        if (statusprot != null) {
            if (estadoprot[i] == "<font size = 4px><font color = green>Normal") {
                if (typeof statusprot[labelprot[i]] != 'undefined') {
                    if (statusprot[labelprot[i]].H == true)
                        estadoprot[i] = "<font size = 4px><font color = orange><b>Alarma H<b>";
                    if (statusprot[labelprot[i]].L == true)
                        estadoprot[i] = "<font size = 4px><font color = orange><b>Alarma L<b>";
                    if (statusprot[labelprot[i]].HH == true)
                        estadoprot[i] = "<font size = 4px><font color = red><b>Disparo HH<b>";
                    if (statusprot[labelprot[i]].LL == true)
                        estadoprot[i] = "<font size = 4px><font color = red><b>Disparo LL<b>";
                }
            }
        }
    }
}

