// Protecciones PQA.
var Voltage = global.get("PQA.Real_Time_Parameters.Average_Line_Voltage", 'memoryOnly') || 0;
var Selection = flow.get("proteccionSelec", 'memoryOnly') || "-";

// Protecciones Variador
var Load = global.get("C2S.Implicit.Output_Current", 'memoryOnly') || 0;

var Rtx = global.get("C2S.RelTx") || 1;
var MtrCurrent = Load / Rtx;

var BusDC = global.get("C2S.Implicit.Bus_DC_Volts", 'memoryOnly') || 0;

// Protecciones Instrumentación
var THP = global.get("Process_data.AI['Presión de cabeza THP']", 'memoryOnly') || 0;
var THT = global.get("Process_data.AI['Temperatura de cabeza THT']", 'memoryOnly') || 0;

// Sensor de Fondo
var Process_data = global.get("Process_data.DHS", 'memoryOnly')||[];
var Vibration_X = Process_data["Vibración X"] || 0;
var Vibration_Z = Process_data["Vibración Z"] || 0;

//Protecciones Fondo
var Vibration_Gral = Vibration_X >= Vibration_Z ? Vibration_X : Vibration_Z;
var LeakageCurrent = Process_data["Corriente de fuga"] || 0;
var IntakePressure = Process_data["Presión de entrada"] || 0;
var DischargePressure = Process_data["Presión de descarga"] || 0;
var IntakeTemperature = Process_data["Temperatura de entrada"] || 0;
var MotorTemperature = Process_data["Temperatura de motor"] || 0;
var DischargeTemperature = Process_data["Temperatura de descarga"] || 0;

var CurrentValue;
if (Selection == "Voltage") { CurrentValue = DecPlacces(Voltage, 2) + " V"; }
else if (Selection == "Load") { CurrentValue = DecPlacces(MtrCurrent, 2) + " A"; }
else if (Selection == "BusDC") { CurrentValue = DecPlacces(BusDC, 2) + " V"; }
else if (Selection == "THP") { CurrentValue = DecPlacces(THP, 2) + " psi"; }
else if (Selection == "THT") { CurrentValue = DecPlacces(THT, 2) + " ºF"; }
else if (Selection == "VibrationGeneral") { CurrentValue = DecPlacces(Vibration_Gral, 2) + " G"; }
else if (Selection == "LeakageCurrent") { CurrentValue = DecPlacces(LeakageCurrent, 2) + " mA"; }
else if (Selection == "IntakePressure") { CurrentValue = DecPlacces(IntakePressure, 2) + " psi"; }
else if (Selection == "DischargePressure") { CurrentValue = DecPlacces(DischargePressure, 2) + " psi"; }
else if (Selection == "IntakeTemperature") { CurrentValue = DecPlacces(IntakeTemperature, 2) + " ºF"; }
else if (Selection == "MotorTemperature") { CurrentValue = DecPlacces(MotorTemperature, 2) + " ºF"; }
else if (Selection == "DischargeTemperature") { CurrentValue = DecPlacces(DischargeTemperature, 2) + " ºF"; }
else { CurrentValue = "---"; }

return [
    { payload: DecPlacces(Voltage, 1) },
    { payload: DecPlacces(MtrCurrent, 1) },
    { payload: DecPlacces(BusDC, 1) },
    { payload: DecPlacces(THP, 2) },
    { payload: DecPlacces(THT, 2) },
    { payload: DecPlacces(Vibration_Gral, 3) },
    { payload: DecPlacces(LeakageCurrent, 3) },
    { payload: DecPlacces(IntakePressure, 1) },
    { payload: DecPlacces(DischargePressure, 1) },
    { payload: DecPlacces(IntakeTemperature, 1) },
    { payload: DecPlacces(MotorTemperature, 1) },
    { payload: DecPlacces(DischargeTemperature, 1) },
    { payload: CurrentValue }
];

function DecPlacces(value, Places) {
    return Math.abs(parseFloat(value.toFixed(Places)));
}

