function TON(val,delay){
    var Ant = context.get("Anterior")||false;
    // Actualizar el contexto
    context.set("Anterior", val);
    //Flanco de subida
    if(val === true && Ant === false){
        context.set("RefTime", Date.now());
    }
    else if(val === true && Ant === true){
        if(Date.now() - context.get("RefTime") >= delay*1000){
            return true;
        }
    }
    return false;
}

