function Escalizar(a,value){
    var b = [4,20];
    // Mapeo
    if(b !== null && typeof a[0] == 'number' && typeof a[1] == 'number' ){
        var t = b[0] + ((Math.abs(value) - a[0])*(b[1] - b[0])) / (a[1]-a[0]);
        // Saturación
        if(t > b[1]){    
            t = b[1];
        }
        else if(t < b[0]){ 
            t = b[0];
        }
        // Asignar payload
        return t;
    }
    else {
        // Asignar payload
        return 0;
    }
}

