function Escalizacion(value, ScaleA, ScaleB) {
    if (ScaleB !== null) {
        var t = (ScaleB[0] + ((value - ScaleA[0]) * (ScaleB[1] - ScaleB[0])) / (ScaleA[1] - ScaleA[0]));
        // Saturación
        if (t > ScaleB[1]) { t = ScaleB[1]; }
        else if (t < ScaleB[0]) { t = ScaleB[0]; }
        // Asignar payload
        return t.toFixed(1);
    }
    else {
        // Asignar payload
        return 0;
    }
}

