var Uint8 = new Uint8Array(6);
var NewestEvent = msg.payload[0];
var StartingGroup = msg.payload[1];
var Timestamp1 = msg.payload[3];
var Timestamp2 = msg.payload[4];
var Timestamp3 = msg.payload[5];
var Timestamp4 = msg.payload[6];
var VSagVSwell = msg.payload[7];
var RatedValue = msg.payload[8];
var Threshold = msg.payload[9];
var HalfCycle = msg.payload[10];

//Corrimiento de datos
Uint8[0] = Timestamp1; //Month
Uint8[1] = Timestamp1 >> 8;
Uint8[2] = Timestamp2; //Hora
Uint8[3] = Timestamp2 >> 8; //Day
Uint8[4] = Timestamp3;  //Second
Uint8[5] = Timestamp3 >> 8; //Minute

var VSagVSwell1 = "";
//Define Power Sag o Power Swell
if (Threshold <= 100 ){
     VSagVSwell1 = "Voltaje Sag";
} else {
    VSagVSwell1 = "Voltaje Swell";
} context.set("VSagVSwell1", VSagVSwell1, "memoryOnly");

//Formato de Timestamp 
var year = Uint8[1] + 2000;
if (Uint8[0] < 10) { // Para el mes
    var Month = '0' + Uint8[0];
} else { Month = Uint8[0]; }
if (Uint8[3] < 10) { // Para el día
    var Day = '0' + Uint8[3];
} else {Day = Uint8[3];}
if (Uint8[2] < 10) { // Para la Hora
    var Hora = '0' + Uint8[2];
} else { Hora = Uint8[2]; }
if (Uint8[5] < 10) { // Para los Minutos
    var Minute = '0' + Uint8[5];
} else { Minute = Uint8[5]; }
if (Uint8[4] < 10) { // Para los Segundos
    var Second = '0' + Uint8[4];
} else { Second = Uint8[4]; }


//Data del evento consultado - estructura
var dataEvento = { 
    "NewestEvent": NewestEvent,
    "StartingGroup": StartingGroup,
    "Fecha": year + "/" + Month + "/" + Day,
    "Hora": Hora + ":" + Minute + ":" + Second,
    "Milisegundos": Timestamp4,
    "VSagVSwell": VSagVSwell1,
    "RatedValue": RatedValue,
    "Threshold": Threshold,
    "HalfCycle": HalfCycle
};
context.set("dataEvento",dataEvento,"memoryOnly");
