//Consultar los eventos 
var Escritura = flow.get("Escritura", "memoryOnly") || (NewestEvent);
var m = context.get("m", "memoryOnly") || 0;
context.set("NewestEvent", NewestEvent,"memoryOnly");
context.set("Escritura", Escritura,"memoryOnly");
var EventoGuardado = flow.get("EventoGuardado", "memoryOnly") || [];
EventoGuardado[0] = null;

if (m >= 0 && m <= 20){
    if (m == 0) {
            Escritura = NewestEvent;
            EventoGuardado[m] = dataEvento;
            context.set("EventoGuardado", EventoGuardado, "memoryOnly");
            flow.set("EventoGuardado", EventoGuardado, "memoryOnly");
    } else if (m >= 1) {
        Escritura = NewestEvent - m;
        EventoGuardado[m] = dataEvento;
        context.set("EventoGuardado", EventoGuardado, "memoryOnly");
        flow.set("EventoGuardado", EventoGuardado, "memoryOnly");
    } m = m + 1;
} else {
    m = 0;
    Escritura = NewestEvent;
}context.set("m",m,"memoryOnly");

EventoGuardado[0] = null;
msg.payload = EventoGuardado; //Visualizacion en la tabla 

flow.set("Escritura", Escritura, "memoryOnly"); //Guarda el numero del evento a consultar 

